import { useCallback, useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useSpring, animated } from "@react-spring/web";
import "./ADHDResources.css";

const ADHDResources = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [message, setMessage] = useState("");
  const footerRef = useRef(null);
  const [typedText, setTypedText] = useState("");
  const [isTyping, setIsTyping] = useState(false);

  // Navigation handlers
  const onButtonContainerClick = useCallback(() => {
    navigate("/contact");
  }, [navigate]);

  const onButtonContainerClick2 = useCallback(() => {
    navigate("/about-us");
  }, [navigate]);

  const onButtonContainerClick3 = useCallback(() => {
    navigate("/adhd-resources");
  }, [navigate]);

  const onButtonContainerClick4 = useCallback(() => {
    navigate("/your-results");
  }, [navigate]);

  const onButtonContainerClick5 = useCallback(() => {
    navigate("/login");
  }, [navigate]);

  const handleLogout = () => {
    if (window.confirm("Are you sure you want to log out?")) {
      signOut(auth)
        .then(() => {
          console.log("User logged out");
          window.location.reload();
        })
        .catch((error) => {
          console.error("Error logging out:", error);
        });
    }
  };

  // Animation for fading in content
  const fadeProps = useSpring({
    opacity: 1,
    transform: "translateY(0px)",
    from: { opacity: 0, transform: "translateY(100px)" },
    config: { duration: 800 },
  });


  // Typing animation effect
  const fullText = "ADDHDR: integrating gamification and dynamic questionnaires to simplify the diagnosis process";

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsTyping(true);
        }
      },
      { threshold: 0.5 }
    );
    if (footerRef.current) {
      observer.observe(footerRef.current);
    }
    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    if (!isTyping) return;

    let index = 0;
    setTypedText("");
    const typingInterval = setInterval(() => {
      if (index < fullText.length) {
        setTypedText((prev) => prev + fullText.charAt(index));
        index++;
      } else {
        clearInterval(typingInterval);
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, [isTyping]);



  return (
    <div className="ADHD-Resources-page">
      <div className="home">
        {/* Header Section */}
        <div className="header">
          <div className="adhdr-container">
            <div className={`nav-item ${window.location.pathname === "/" ? "nav-item-active" : ""}`}>
              ADHDR
            </div>
          </div>

          <div className="about-container">
            <div className={`nav-item ${window.location.pathname === "/about-us" ? "nav-item-active" : ""}`} onClick={onButtonContainerClick2}>
              About Us
            </div>
          </div>

          <div className="results-container">
            <div className={`nav-item ${window.location.pathname === "/about-us" ? "nav-item" : ""}`} onClick={onButtonContainerClick4}>
              Your Results
            </div>
          </div>

          <div className="resources-container">
            <div className={`nav-item ${window.location.pathname === "/about-us" ? "nav-item-active" : ""}`} onClick={onButtonContainerClick3}>
              ADHD Resources
            </div>
          </div>

          <div className="contact-container">
            <div className={`nav-item ${window.location.pathname === "/contact" ? "nav-item-active" : ""}`} onClick={onButtonContainerClick}>
              Contact Us
            </div>
          </div>

          <div className="contact-container">
            {isLoggedIn ? (
              <div className="nav-item" onClick={handleLogout}>
                Log Out
              </div>
            ) : (
              <div className={`nav-item ${window.location.pathname === "/login" ? "nav-item-active" : ""}`} onClick={onButtonContainerClick5}>
                Login or Sign Up
              </div>
            )}
          </div>
        </div>

        {/* Main Content Section */}
         <animated.div style={fadeProps} className="content-wrapper">
          <div className="about-title">
            <h1>ADHD Resources</h1>
            <p className="tagline">
              Exploring innovative solutions for managing ADHD with technology!
            </p>
          </div>

          <article className="resources-content">
            <section>
              <h2>What is ADHD?</h2>
              <p>
              Attention-Deficit/Hyperactivity Disorder (ADHD) is a common, lifelong neurodevelopmental disorder typically diagnosed in childhood. Symptoms such as inattention and hyperactivity can significantly impact relationships and social life. ADHD is categorized into three types: hyperactive/impulsive, inattentive, and combined. Research indicates that genetic factors may contribute to ADHD, and it is more frequently diagnosed in biological males. However, females with ADHD often have the inattentive type, which can lead to underdiagnosis. Understanding ADHD involves recognizing its diverse manifestations and ongoing research into its genetic and gender-related aspects.

              </p>
            </section>

            <section>
              <h2>Symptoms in Children</h2>
              <p>
              Symptoms in children tend to show before the age of 6 and will be present in more than one setting, such as at school and home. Children may display a combination of inattentiveness, hyperactivity, and impulsivity or just one of the three. 
The main signs of inattentiveness are:
<br>
- having a short attention span and being easily distracted
</br>
<br>
- making careless mistakes – for example, in schoolwork
</br>
<br>
- appearing forgetful or losing things
</br>
<br>- being unable to stick to tasks that are tedious or time-consuming</br>
<br>- appearing to be unable to listen to or carry out instructions</br>
<br>- constantly changing activity or task</br>
- having difficulty organizing tasks
- The main signs of hyperactivity and impulsiveness are:
- being unable to sit still, especially in calm or quiet surroundings
- constantly fidgeting
- being unable to concentrate on tasks
- excessive physical movement
- excessive talking
- being unable to wait their turn
- acting without thinking
- interrupting conversations
- little or no sense of danger
              </p>
            </section>

            <section>
              <h2>Treatment Options</h2>
              <p>
                ADHD treatment often involves a combination of medication and behavioral
                therapy. Medications can help control symptoms, while therapy can teach
                valuable coping strategies and organizational skills. Additionally,
                lifestyle changes such as regular exercise, adequate sleep, and healthy
                eating habits can help manage symptoms.
              </p>
            </section>

            <section>
              <h2>Support and Strategies</h2>
              <p>
                Living with ADHD often requires developing personalized strategies for
                success. This might include using organizational tools, breaking tasks
                into smaller steps, creating structured routines, and utilizing
                reminder systems. Support from family, educators, and healthcare
                providers is also crucial for managing ADHD effectively.
              </p>
            </section>

            <section>
              <h2>Our Approach</h2>
              <p>
                Traditional diagnostic methods often struggle to fully understand the
                complex and changing nature of ADHD symptoms, especially in younger and
                neurodiverse individuals. We aim to enhance ADHD diagnosis through
                gamification and adaptive assessment, making the process more
                engaging and accurate while reducing stress for participants.
              </p>
            </section>
          </article>
        </animated.div>

        {/* Footer Section */}
        <div className="navigation-footer1" ref={footerRef}>
          <div className="adhdr3">
            <p className="adhdr4">
              {typedText}
              {typedText.length < fullText.length && <span className="ellipsis">...</span>}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ADHDResources;
