import { useCallback, useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useSpring, animated } from "@react-spring/web";
import { Card, CardHeader, CardContent, CardTitle } from "@/components/ui/card";
import { BookOpen, Users, Calendar, Video, ExternalLink } from "lucide-react";
import { auth, signOut, onAuthStateChanged } from "./firebaseConfig"; // Assuming firebase config is in this file

const ADHDResources = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [message, setMessage] = useState("");
  const footerRef = useRef(null);
  const [typedText, setTypedText] = useState("");
  const [isTyping, setIsTyping] = useState(false);

  // Navigation handlers consolidated into a single function
  const handleNavigation = useCallback((path) => {
    navigate(path);
  }, [navigate]);

  const handleLogout = async () => {
    if (window.confirm("Are you sure you want to log out?")) {
      try {
        await signOut(auth);
        console.log("User logged out");
        window.location.reload();
      } catch (error) {
        console.error("Error logging out:", error);
      }
    }
  };

  // Animation for fading in content
  const fadeProps = useSpring({
    opacity: 1,
    transform: "translateY(0px)",
    from: { opacity: 0, transform: "translateY(100px)" },
    config: { duration: 800 },
  });

  // Resources data
  const resources = [
    {
      title: "Understanding ADHD Basics",
      description: "A comprehensive guide to ADHD symptoms, diagnosis, and treatment options.",
      link: "https://example.com/adhd-guide",
      icon: <BookOpen className="h-6 w-6" />
    },
    {
      title: "ADHD Support Groups",
      description: "Find local and online support groups for individuals with ADHD.",
      link: "https://example.com/support-groups",
      icon: <Users className="h-6 w-6" />
    },
    {
      title: "ADHD Management Strategies",
      description: "Practical tips and techniques for managing ADHD symptoms in daily life.",
      link: "https://example.com/management",
      icon: <Calendar className="h-6 w-6" />
    },
    {
      title: "Video Resources",
      description: "Educational videos about living with and understanding ADHD.",
      link: "https://example.com/videos",
      icon: <Video className="h-6 w-6" />
    }
  ];

  const fullText = "ADHDR: integrating gamification and dynamic questionnaires to simplify the diagnosis process";

  // Typing animation effect
  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsTyping(true);
        }
      },
      { threshold: 0.5 }
    );

    const currentRef = footerRef.current;
    if (currentRef) {
      observer.observe(currentRef);
    }

    return () => {
      if (currentRef) {
        observer.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    if (!isTyping) return;

    let index = 0;
    setTypedText("");
    const typingInterval = setInterval(() => {
      if (index < fullText.length) {
        setTypedText((prev) => prev + fullText.charAt(index));
        index++;
      } else {
        clearInterval(typingInterval);
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, [isTyping, fullText]);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setIsLoggedIn(!!user);
      setMessage(user ? "You are now logged in." : "");
    });
    return () => unsubscribe();
  }, []);

  const NavItem = ({ path, label, onClick }) => (
    <div className="nav-item-container">
      <div 
        className={`nav-item ${window.location.pathname === path ? "nav-item-active" : ""}`}
        onClick={onClick}
      >
        {label}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header Section */}
        <nav className="flex items-center justify-between py-6">
          <NavItem path="/" label="ADHDR" onClick={() => handleNavigation("/")} />
          <NavItem path="/about-us" label="About Us" onClick={() => handleNavigation("/about-us")} />
          <NavItem path="/your-results" label="Your Results" onClick={() => handleNavigation("/your-results")} />
          <NavItem path="/adhd-resources" label="ADHD Resources" onClick={() => handleNavigation("/adhd-resources")} />
          <NavItem path="/contact" label="Contact Us" onClick={() => handleNavigation("/contact")} />
          {isLoggedIn ? (
            <NavItem label="Log Out" onClick={handleLogout} />
          ) : (
            <NavItem path="/login" label="Login or Sign Up" onClick={() => handleNavigation("/login")} />
          )}
        </nav>

        {/* Main Content Section */}
        <animated.div style={fadeProps} className="py-12">
          <div className="text-center mb-12">
            <h1 className="text-4xl font-bold mb-4">ADHD Resources</h1>
            <p className="text-xl text-gray-600">
              Helpful resources and information for understanding and managing ADHD
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {resources.map((resource, index) => (
              <Card key={index} className="hover:shadow-lg transition-shadow duration-300">
                <CardHeader>
                  <div className="flex items-center gap-3">
                    {resource.icon}
                    <CardTitle className="text-xl">{resource.title}</CardTitle>
                  </div>
                </CardHeader>
                <CardContent>
                  <p className="text-gray-600 mb-4">{resource.description}</p>
                  <a
                    href={resource.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-flex items-center text-blue-600 hover:text-blue-800"
                  >
                    Learn More <ExternalLink className="ml-2 h-4 w-4" />
                  </a>
                </CardContent>
              </Card>
            ))}
          </div>
        </animated.div>

        {/* Footer Section */}
        <footer ref={footerRef} className="py-8 mt-12 border-t border-gray-200">
          <p className="text-center text-gray-600">
            {typedText}
            {typedText.length < fullText.length && <span className="animate-pulse">...</span>}
          </p>
        </footer>
      </div>
    </div>
  );
};

export default ADHDResources;
