import { useCallback, useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useSpring, animated } from "@react-spring/web";
import "./AboutUs.css";

const ADHDResources = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [message, setMessage] = useState("");
  const footerRef = useRef(null);
  const [typedText, setTypedText] = useState("");
  const [isTyping, setIsTyping] = useState(false);

  // Navigation handlers
  const onButtonContainerClick = useCallback(() => {
    navigate("/contact");
  }, [navigate]);

  const onButtonContainerClick2 = useCallback(() => {
    navigate("/about-us");
  }, [navigate]);

  const onButtonContainerClick3 = useCallback(() => {
    navigate("/adhd-resources");
  }, [navigate]);

  const onButtonContainerClick4 = useCallback(() => {
    navigate("/your-results");
  }, [navigate]);

  const onButtonContainerClick5 = useCallback(() => {
    navigate("/login");
  }, [navigate]);

  const handleLogout = () => {
    if (window.confirm("Are you sure you want to log out?")) {
      signOut(auth)
        .then(() => {
          console.log("User logged out");
          window.location.reload();
        })
        .catch((error) => {
          console.error("Error logging out:", error);
        });
    }
  };

  // Animation for fading in content
  const fadeProps = useSpring({
    opacity: 1,
    transform: "translateY(0px)",
    from: { opacity: 0, transform: "translateY(100px)" },
    config: { duration: 800 },
  });

  // Resources data
  const resources = [
    {
      title: "Understanding ADHD Basics",
      description: "A comprehensive guide to ADHD symptoms, diagnosis, and treatment options.",
      link: "https://example.com/adhd-guide",
      icon: <BookOpen className="h-6 w-6" />
    },
    {
      title: "ADHD Support Groups",
      description: "Find local and online support groups for individuals with ADHD.",
      link: "https://example.com/support-groups",
      icon: <Users className="h-6 w-6" />
    },
    {
      title: "ADHD Management Strategies",
      description: "Practical tips and techniques for managing ADHD symptoms in daily life.",
      link: "https://example.com/management",
      icon: <Calendar className="h-6 w-6" />
    },
    {
      title: "Video Resources",
      description: "Educational videos about living with and understanding ADHD.",
      link: "https://example.com/videos",
      icon: <Video className="h-6 w-6" />
    }
  ];

  // Typing animation effect
  const fullText = "ADHDR: integrating gamification and dynamic questionnaires to simplify the diagnosis process";

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsTyping(true);
        }
      },
      { threshold: 0.5 }
    );
    if (footerRef.current) {
      observer.observe(footerRef.current);
    }
    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    if (!isTyping) return;

    let index = 0;
    setTypedText("");
    const typingInterval = setInterval(() => {
      if (index < fullText.length) {
        setTypedText((prev) => prev + fullText.charAt(index));
        index++;
      } else {
        clearInterval(typingInterval);
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, [isTyping]);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsLoggedIn(true);
        setMessage("You are now logged in.");
      } else {
        setIsLoggedIn(false);
        setMessage("");
      }
    });
    return () => unsubscribe();
  }, []);

  return (
    <div className="ADHD-Resources-page">
      <div className="home">
        {/* Header Section */}
        <div className="header">
          <div className="adhdr-container">
            <div className={`nav-item ${window.location.pathname === "/" ? "nav-item-active" : ""}`}>
              ADHDR
            </div>
          </div>

          <div className="about-container">
            <div className={`nav-item ${window.location.pathname === "/about-us" ? "nav-item-active" : ""}`} onClick={onButtonContainerClick2}>
              About Us
            </div>
          </div>

          <div className="results-container">
            <div className={`nav-item ${window.location.pathname === "/about-us" ? "nav-item" : ""}`} onClick={onButtonContainerClick4}>
              Your Results
            </div>
          </div>

          <div className="resources-container">
            <div className={`nav-item ${window.location.pathname === "/about-us" ? "nav-item-active" : ""}`} onClick={onButtonContainerClick3}>
              ADHD Resources
            </div>
          </div>

          <div className="contact-container">
            <div className={`nav-item ${window.location.pathname === "/contact" ? "nav-item-active" : ""}`} onClick={onButtonContainerClick}>
              Contact Us
            </div>
          </div>

          <div className="contact-container">
            {isLoggedIn ? (
              <div className="nav-item" onClick={handleLogout}>
                Log Out
              </div>
            ) : (
              <div className={`nav-item ${window.location.pathname === "/login" ? "nav-item-active" : ""}`} onClick={onButtonContainerClick5}>
                Login or Sign Up
              </div>
            )}
          </div>
        </div>

        {/* Main Content Section */}
        <animated.div style={fadeProps}>
          <div className="content-wrapper">
            <div className="resources-title">
              <h1 className="text-4xl font-bold text-center mb-8">ADHD Resources</h1>
              <p className="text-xl text-center text-gray-600 mb-12">
                Helpful resources and information for understanding and managing ADHD
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-6 px-4">
              {resources.map((resource, index) => (
                <Card key={index} className="hover:shadow-lg transition-shadow duration-300">
                  <CardHeader>
                    <div className="flex items-center gap-3">
                      {resource.icon}
                      <CardTitle className="text-xl">{resource.title}</CardTitle>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <p className="text-gray-600 mb-4">{resource.description}</p>
                    <a
                      href={resource.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-flex items-center text-blue-600 hover:text-blue-800"
                    >
                      Learn More <ExternalLink className="ml-2 h-4 w-4" />
                    </a>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        </animated.div>

        {/* Footer Section */}
        <div className="navigation-footer1" ref={footerRef}>
          <div className="adhdr3">
            <p className="adhdr4">
              {typedText}
              {typedText.length < fullText.length && <span className="ellipsis">...</span>}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ADHDResources;
